import React , { useEffect, useState }from "react";
import ReactDOM from "react-dom"

// import Header from "./components";

const baseURL = "https://strangers-things.herokuapp.com/api/2206-ftb-et-web-ft-b"
const App = () => {
    const [posts, setPosts] = useState([]);
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [currentUser, setCurrentUser] = useState({})

    useEffect (() => {
        const fetchPosts = async () => {
            try {
                const response = await fetch(`${baseURL}/posts`);
                const postsJ = await response.json();
                console.log("got posts: ", postsJ);
                setPosts(postsJ.data.posts);
            } catch (error) {
                console.error(error)
            }
        };
        fetchPosts();
    }, []);

    return (
        <div>
            {/* <Header posts={posts}/> */}
           <p>dfsfds</p>
           {
            posts.length ? posts.map((eachPost, idx) => {
                return <div key={idx} style={{border: "2px solid lavendar"}}>
                    <p> {eachPost.title} </p>
                    <p> {eachPost.description} </p>
                    <p>Price: {eachPost.price} </p>
                    <p>Seller: {eachPost.author.username} </p>
                </div>
            }) : <div>No posts</div>
           }
        </div>
    )
}

const appElement = document.getElementById("app");
ReactDOM.render(<App />, appElement);
